      "title": "Versiyon Takip Sistemi ve Changelog Eklentisi",
      "changes": [
        {
          "type": "feature",
          "description": "Otomatik versiyon takip sistemi eklendi",
          "details": "Footer'da tıklanabilir versiyon bilgisi ve detaylı changelog modal'ı"
        },
        {
          "type": "backend",
          "description": "Changelog helper fonksiyonları oluşturuldu",
          "details": "updateChangelog(), addChangelogEntry(), autoUpdateChangelog() fonksiyonları"
        },
        {
          "type": "backend",
          "description": "Home::getChangelog() API endpoint'i eklendi",
          "details": "Changelog verilerini JSON formatında döndüren AJAX endpoint"
        },
        {
          "type": "ui",
          "description": "Footer versiyon alanı yeniden tasarlandı",
          "details": "Git branch ikonu ve tıklanabilir versiyon linki eklendi"
        },
        {
          "type": "feature",
          "description": "Changelog modal arayüzü oluşturuldu",
          "details": "Kategorik değişiklik tipleri, dosya listesi, versiyon geçmişi"
        }
      ],
      "files_modified": [
        "application/views/include/footer-js.php",
        "application/controllers/Home.php",
        "application/helpers/changelog_helper.php",
        "application/config/autoload.php",
        "application/config/changelog.json"
      ],
      "db_changes": false
    },
    {
